#pragma config(Motor,  port1,           Backright,     tmotorVex393_HBridge, openLoop)
#pragma config(Motor,  port5,           Backleft,      tmotorVex393_MC29, openLoop, reversed)
#pragma config(Motor,  port6,           Frontright,    tmotorVex393_MC29, openLoop)
#pragma config(Motor,  port10,          Frontleft,     tmotorVex393_HBridge, openLoop, reversed)
