task main()
{
	while(true) //forever
	{
		if(SensorValue(limitswitch) == 1)
		{
			wait(2);
			startMotor(Backleft, 44);
			startMotor(Backright, 44);
			startMotor(Frontleft, 44);
			startMotor(Frontright, 44);
			wait(4);
		}
		stopMotor(Backleft);
		stopMotor(Backright);
		stopMotor(Frontleft);
		stopMotor(Frontright);

		turnright(x, y);

	}
}
